INSERT INTO `forumposts` VALUES ('1', '12301640175070', '4', '雪具在小镇吃饭，非常方便', '/upload/20221230/fc0b8f0d-b2b2-4993-87e4-4c93797230c3.png', '<p class=\"_j_note_content _j_seqitem\" data-seq=\"1838473063\" style=\"margin-top: 0px; margin-bottom: 20px; padding: 0px; overflow-wrap: break-word; line-height: 28px; font-size: 15px; color: rgb(68, 68, 68); text-align: justify; word-break: break-word; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; white-space: normal;\">在这只有滑雪是正经事，午饭随便买了点红薯和&nbsp;<a href=\"https://www.mafengwo.cn/travel-scenic-spot/mafengwo/60211.html\" class=\"link _j_keyword_mdd\" data-kw=\"关东\" target=\"_blank\" style=\"color: rgb(77, 77, 77); cursor: pointer; outline: 0px; border-bottom: 1px solid rgb(148, 142, 142);\">关东</a>&nbsp;煮对付了下，就开始上魔毯热身啦！魔毯虽然号称初级道，但其实因为人多所以雪被压很硬，非常呲溜，而且坡度也挺陡，加上雪道短，上上下下很浪费时间，所以不是很推荐，有基础的不如胆子大一点直接上中级道，只要做好防护、学会拐弯减速和刹车，哪怕新手也完全没有问题，甚至比魔毯更好滑~</p><div id=\"_j_photo_1838387032\" data-seq=\"1838387093\" data-pid=\"1838387032\" class=\"add_pic _j_anchorcnt _j_seqitem\" style=\"margin: 0px 0px 18px; padding: 0px; overflow-wrap: break-word; position: relative; color: rgb(68, 68, 68); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 15px; white-space: normal; width: 680px;\"><a target=\"_blank\" href=\"https://www.mafengwo.cn/photo/10072/scenery_23984536/1838387032.html\" style=\"color: rgb(255, 138, 0); cursor: pointer; outline: 0px; display: block; position: relative; zoom: 1;\"><img class=\"\" src=\"https://note.mafengwo.net/img/fa/21/e254651254ac54547ec791c7280f4fe3.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90%7CimageMogr2%2Fstrip%2Fquality%2F90\" data-src=\"https://note.mafengwo.net/img/fa/21/e254651254ac54547ec791c7280f4fe3.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90%7Cimage